import React, { useState, useEffect, useCallback } from 'react'

import MoviesList from './components/MoviesList'
import './App.css'

function App() {
  const [movies, setMovies] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState(null)

  //sendHttpRequest function is an asynchronous function
  //A http request is a side effect

  const sendHttpRequest = useCallback(async () => {
    setIsLoading(true)
    //To make sure that we clear any previous errors we might have gotten.
    setError(null)

    try {
      //fetch API returns a promise
      //await keyword is used instead of the then-catch block
      const response = await fetch('https://swapi.dev/api/films/')

      if (!response.ok) {
        throw new Error('Something went wrong!')
      }

      //the JSON data is converted to JavaScript data
      //This also returns a promise
      const data = await response.json()

      //ok field signals whether the response was successful or not.

      const transformedMovies = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        }
      })
      setMovies(transformedMovies)
    } catch (error) {
      setError(error.message)
    }
    setIsLoading(false)
  }, [])
  useEffect(() => {
    sendHttpRequest()
  }, [sendHttpRequest])

  let content = <p>Found no movies.</p>

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />

    if (isLoading) {
      content = <p>Loading...</p>
    }

    if (error) {
      content = <p>{error}</p>
    }
  }
  return (
    <React.Fragment>
      <section>
        <button onClick={sendHttpRequest}>Fetch Movies</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  )
}

export default App
